class Solution {
    func combinationSum2(_ candidates: [Int], _ target: Int) -> [[Int]] {

        var candidates = candidates.sorted()
        var res = [[Int]]()
        var temp = [Int]()

        helper(&res, &temp, candidates, 0, target)

        return res
    }

    func helper(_ res: inout [[Int]], _ temp: inout [Int], _ candidates: [Int], _ index: Int, _ target: Int) {

        if target == 0 { res.append(temp); return }
        if target < 0 { return }

        for i in index..<candidates.count {

            //Avoid dupes.
            if i > index && candidates[i] == candidates[i - 1] { 
                continue
            }
            temp.append(candidates[i])
            helper(&res, &temp, candidates, i + 1, target - candidates[i])
            temp.removeLast()
        }
    }
}
