        return;
    }

    for(int i=index;i<candidates.size();i++){
        if(i>index && candidates[i]==candidates[i-1]){
            continue;
        }
        v.push_back(candidates[i]);
        combinationSum_helper(candidates,target-candidates[i],v,ans,i+1);
        v.pop_back();
