        //choose current elemnent
        sum += candidates[i];
        temp.emplace_back(candidates[i]);
        solve(candidates , target , i+1 , ans , temp , sum); //not moving index as we can choose same element twice
        temp.pop_back();    //remove the chosen item for other option
        sum -= candidates[i];   //decrease current item value
        solve(candidates , target , i+1 ,ans, temp , sum);  //move to next item 
