class Solution {
public:
bool isValid(vector<vector<int>>& ans , vector<int>& temp)
{
    for(int i = 0;i< ans.size();++i)
    {
        if(ans[i] == temp) return false;
    }
    return true;
}
    void solve(vector<int>& candidates , int target,int i ,vector<vector<int>>& ans , vector<int>& temp,int sum)
    {
        if(i >= candidates.size())//index out of bound
        {
            return;
        }
        if(sum == target) //valid answer found 
        {
            sort(temp.begin(),temp.end());
            if(isValid(ans , temp)){
            ans.emplace_back(temp);}
            return;
        }
        else if(sum > target) return;   //ans not possible go back 

        //choose current elemnent
        sum += candidates[i];
        temp.emplace_back(candidates[i]);
        solve(candidates , target , i+1 , ans , temp , sum); //not moving index as we can choose same element twice
        temp.pop_back();    //remove the chosen item for other option
        sum -= candidates[i];   //decrease current item value
        solve(candidates , target , i+1 ,ans, temp , sum);  //move to next item 
    }
    vector<vector<int>> combinationSum2(vector<int>& candidates, int target) {
        vector<vector<int>> ans;
        vector<int>temp;
        int sum = 0;
        sort(candidates.begin() , candidates.end());
        solve(candidates , target , 0 , ans , temp , sum);
        return ans;
    }
};



