struct Node{
	bool end=false;
	Node* charset[26];

	bool containsChar(char ch){
		return charset[ch-'a']!= NULL;
	}

	Node* getNode(char ch){
		return charset[ch-'a'];
	}

	void putChar(char ch, Node* node){
		charset[ch-'a']= node;
	}

};


class Trie{
	public:
		Node* root;
	public: 
		Trie(){
			root = new Node();
		}
	public:
		void insertNode(string s){
			Node* node = root;
			for(int i=0; i<s.size(); i++){
				if(!node->containsChar(s[i])){
						node->putChar(s[i], new Node());
				}
				node = node->getNode(s[i]);
			}
			node->end = true;
		}
};



class Solution {

	// sentences function returns the possible sentences as vector of its words,
	// using words from wordDict and considering the substring
	// of s  from position pos to end of s .


public:
	vector<vector<string>> sentences(string &s,int pos,Node* root,unordered_map<int,vector<vector<string>>> &mem){
		if(pos>=s.size())
			return {{""}};

		if(mem.find(pos) != mem.end())
			return mem[pos];

		vector<vector<string>> ans,subans;
		string temp="";

		// from pos we iterate and try to find a word in wordDict if exists then move to next substring 
		// and get the possible sentences from it, then append the word found in current call to it.
		// say we found that cat is a word from wordDict, 
		// again if we find cats is also a word from wordDict, checking it will take O(1) if we use trie
		// we don't have to check for cats once we found cat , we only check for s after cat
		Node* node = root;
		for(int i=pos; i<s.size(); i++){
			temp += s[i];
			node= node->getNode(s[i]);
			// if cats doesn't exist catsa won't exist. 
			if(!node)
				return ans;

			if(node->end){

				subans = sentences(s,i+1, root,mem);

				if(subans.size()>0){
					for(int i=0; i<subans.size(); i++){
						if(subans[i][0].size()==0)
							subans[i][0]= temp;
						else
							subans[i].push_back(temp);

						ans.push_back(subans[i]);
					}
				}
			}
		}

		mem[pos]= ans;
		return ans;
	}
public:
	vector<string> wordBreak(string s, vector<string>& wordDict) {
		Trie trie;
		for(string s: wordDict){
			trie.insertNode(s);
		}

		vector<vector<string>> ans;
		unordered_map<int,vector<vector<string>>> mem;
		ans= sentences(s,0,trie.root,mem);

		vector<string> sentences;

		// make the possible sentences from words of each possible string
		for(vector<string> v: ans){
			string temp= v.back();
			for(int i=v.size()-2; i>=0; i--){
				temp += " "+v[i];
			}
			sentences.push_back(temp);
		}

		return sentences;
	}
};

