class Solution {
public:
    unordered_map<string,vector<string>> memo; //using for memoization (though only recurssive solution can get accepted but for the sake of learning dp)

    vector<string> wordBreak(string s, vector<string>& wordDict) {
        if(memo.find(s) != memo.end()) return memo[s]; //if the target string found in memo, return the vector;

        vector<string> ans; 

        //Base case: s = empty string
        if(s == ""){
            ans.push_back("");
            return ans;
        }

        //else check for every possiblility in wordDict
        //which is found in our target string s

        for(const auto word: wordDict){

            //the word which is found at start will only lead to valid possiblities further
            if(s.find(word) == 0){

                string remain = s.substr(word.length()); //get the remaining substring 

                vector<string> remainAns;

                remainAns = wordBreak(remain , wordDict); //vector<string1,string2, ...>

                for(int i=0; i<remainAns.size(); i++){

                    //add the word to the output string
                    if(remainAns[i] == ""){     
                        remainAns[i] = word;    // e.g 1) remainAns[i] = "dog"
                    }
                    else{
                        remainAns[i] = word + " " + remainAns[i];  //e.g 2) remainAns[i] = "and dog"
                    }
                    ans.push_back(remainAns[i]);
                }
            }
        }
        memo[s] = ans; //storing it in memo for future help
        return ans;
    }
};
