def findWords(start, end, s, wordDict, cache):
    if start in cache:
        return cache[start]
    cache[start] = []
    candidate = ''
    current = start
    while current < end:
        candidate += s[current]
        current += 1
        if candidate in wordDict:
            if current == end:
                cache[start].append(candidate)
            else:
                for x in findWords(current, end, s, wordDict, cache):
                    cache[start].append(candidate + ' ' + x)
    return cache[start]

