                                string newS = tempS + " " + str;
                                dp[j+1].second.insert(newS);
                            }
                        }
                    }
                }
            }
        }

        vector<string> ans;

        //Return all strings for the string ending at (n-1)st index
        for(auto& str : dp[n].second){
            ans.push_back(str);
        }

