class Solution:
    def wordBreak(self, s: str, wordDict: List[str]) -> bool:
        memo = {}
        return self.helper(s, memo, wordDict)
    def helper(self, s, memo, wordDict):
        if s in memo:
            return memo[s]
        ans = []
        if s in wordDict:
            ans.append(s)

        for i in range(1, len(s)):  
            right = s[i:]
            if right not in wordDict:
                continue
            left = s[:i]
            ans += [w + " " + right for w in self.helper(left, memo, wordDict)]

        memo[s] = ans
        return memo[s]


