class Solution {
public:
    struct TrieNode{
        bool isWord;
        unordered_map<char,TrieNode*> children;
        TrieNode(){
            isWord=false;
        }
    };

    void buildTrie(string &s){
        TrieNode* cur=root;
        for(int i=0;i<s.size();i++){
            if(cur->children.find(s[i])==cur->children.end()){
                cur->children[s[i]]=new TrieNode();
            }
            cur=cur->children[s[i]];
        }
        cur->isWord=true;
    }

    vector<string> wordBreak(string s, vector<string>& wordDict) {
        root=new TrieNode();
        for(string &word:wordDict){
            buildTrie(word);
        }
        unordered_map<int,vector<string>> memo;
        return dfs(s,0,memo);
    }

    vector<string> dfs(string &s,int pos,unordered_map<int,vector<string>> &memo){
        vector<string> res;
        if(pos==s.size()){
            res.push_back("");
            return res;
        }

        if(memo.count(pos)){
            return memo[pos];
        }

        TrieNode *cur=root;

        for(int i=pos;i<s.size();i++){
            if(cur->children.find(s[i])==cur->children.end()){
                break;
            }
            cur=cur->children[s[i]];
            if(cur->isWord){
                int len=i-pos+1;
                string tmp=s.substr(pos,len);
                if(i<s.size()-1){
                    tmp+=" ";
                }
                auto next=dfs(s,i+1,memo);
                for(string &n:next){
                    string t=tmp+n;
                    res.push_back(t);
                }                
            }
        }

        memo[pos]=res;
        return res;
    }

private:
    TrieNode* root;
};
