    unordered_map<int, vector<string>> umap;

    vector<string> helper(string &s, unordered_set<string> &dict, int pos) {
        if (umap.find(pos) != umap.end())
            return umap[pos];

        vector<string> res;

        if (dict.find(s.substr(pos)) != dict.end())
            res.push_back(s.substr(pos));

        for (int end = pos + 1; end < s.size(); ++end) {
            string subs = s.substr(pos, end-pos);
            if (dict.find(subs) != dict.end()) {
                vector<string> nextlist = helper(s, dict, end);
                for (auto list_string : nextlist) {
                    res.push_back(subs + " " + list_string);
                }
            }
        }
        umap[pos] = res;
        return umap[pos];
    } 

    vector<string> wordBreak(string s, vector<string>& wordDict) {
        unordered_set<string> dict(wordDict.begin(), wordDict.end());
        return helper(s, dict, 0);
