class Solution {
public:
    unordered_set<string> st;
    void f(int ind,string str,string s,vector<string>& ans){

        if(ind==s.length()){
            ans.push_back(str);
            return ;
        }

        string temp = "";                              //temporary string for finding string in dictionary from ind

        for(int i=ind;i<s.length();i++){
            temp += s[i];
            if(st.count(temp)){
                string k = str;                         //By initialising str to k then we don't need to do backtrack
                if(str.size()!=0) k += " ";         // Edge Case 
                k += temp;
                f(i+1,k,s,ans);
            }
        }
    }

    vector<string> wordBreak(string s, vector<string>& wordDict) {
        for(auto i:wordDict) st.insert(i);
        int ind=0;
        vector<string> ans;
        string str = "";
        f(ind,str,s,ans);                  //Function Call
        return ans;
    }
};
