class Solution:
    def wordBreak(self, s: str, wordDict: List[str]) -> List[str]:

        wordSet = set(wordDict)

        @cache
        def dp(s):

            ## Base case on empty string
            if not s:
                return []

            ## General case
            res = []

            # Type_1: Current s is in wordSet
            if s in wordSet:
                res.append( s ) 

            # Type_2: Decompose s into smaller words in wordSet
            for k in range(1, len(s)):

                postfix = s[k:]
                if postfix not in wordSet: continue

                res += [ prefix + " " + postfix for prefix in dp(s[:k])]

            return res

        # ---------------------------------
        return dp( s )
