from collections import defaultdict

class TrieNode:

    def __init__(self):
        self.children = {}
        self.isWord = False

class Trie:

    def __init__(self):
        self.root = TrieNode()

    def getRoot(self):
        return self.root

    def insertWord(self, word: str):

        nextNode = self.root
        for c in word:
            if c not in nextNode.children:
                nextNode.children[c] = TrieNode()
            nextNode = nextNode.children[c]
        nextNode.isWord = True

class Solution:
    trieNode = None
    s = None
    memoize = None

    def checkConnection(self, prevNode, currentChar: chr) -> TrieNode:
        if currentChar in prevNode.children:
            return prevNode.children[currentChar]
        return None

    def wordBreakHelper(self, index: int, node) -> List[List[str]]:

        if index in self.memoize:
            return self.memoize[index][:]

        result = []
        current = []

        start = index
        while index < len(self.s):
            node = self.checkConnection(node, self.s[index])

            if node is None:
                break

            # valid path
            current.append(self.s[index])

            if node.isWord:
                current_word = ''.join(current)

                if index+1 < len(self.s):
                    tempResult = self.wordBreakHelper(index+1, self.trieNode.getRoot())

                    #print(tempResult, current_word)
                    for val in tempResult:
                        result.append([current_word] + val)
                else:
                    result.append([current_word])

            index += 1

        self.memoize[start] = result
        return self.memoize[start][:]


    def wordBreak(self, s: str, wordDict: List[str]) -> List[str]:

        self.trieNode = Trie()
        self.s = s
        self.memoize = {}
        # creating a Trie

        for s in wordDict:
            self.trieNode.insertWord(s)


        final_result = self.wordBreakHelper(0, self.trieNode.getRoot())


        #print(self.memoize)
        return [' '.join(word_list) for word_list in final_result]
