            for (auto aString : mTemp) {
                for (int i=0;i<aString.size();i++) {
                    mAnsStr[aIndex++] = aString[i]; 
                }
                mAnsStr[aIndex++] = ' ';
            }
            mAnsStr[aIndex - 1] = 0;
            pResult.push_back(string(mAnsStr));
            return;
        }

        for (auto aWord : mMatchSets[pStart]) {
            //Crawl the solution space. Like a baby crawls for milk bottles.
            mTemp.push_back(aWord);
            wordBreakSolutionBuilder(pResult, pString, pStart + aWord.size());   
            mTemp.pop_back();
        }
    }
};
