class Solution {
    List<String> wordDict;
    String s;
    Set<String> res = new HashSet<String>();
    public List<String> wordBreak(String s, List<String> wordDict) {
        this.wordDict = wordDict;
        this.s = s;
        helper(0, 0, new ArrayList<String>());
        return new ArrayList<String>(res);
