    public void helper(int i, int j, List<String> arr) {
        if(j == s.length()) {
            int size = 0;
            for(String c : arr) size += c.length();
            if(size == s.length()) {
                res.add(arr.stream().map(Object::toString)
                          .collect(Collectors.joining(" ")));
            }
            return;
        }
        while(j < s.length()) {
            if(wordDict.contains(s.substring(i, j + 1))) {
                arr.add(s.substring(i, j + 1));
                helper(j + 1, j + 1, arr);
                helper(i, j + 1, arr);
                arr.remove(arr.size() - 1);
            }
            j++;
        }
    }
}
