class Solution:

    def wordBreak(self, s: str, wordDict: List[str]) -> List[str]:
        trie = {}
		#filling the trie
        for word in wordDict:
            currentDict = trie
            for letter in word:
                if letter in currentDict:
                    currentDict = currentDict[letter]
                else:
                    currentDict[letter] = {}
                    currentDict = currentDict[letter]
            currentDict['_'] = 0

        #function to go through the trie    
        def iterate(st, final_res, current, currentTrie):
            for i in range(len(st)):       
                if '_' in currentTrie: #found the end of the word, start new search branch
                    iterate(st[i:], final_res, current + [st[:i]], trie)

                if st[i] in currentTrie:
                    currentTrie = currentTrie[st[i]]
                else: #no possible to fit the word, end this search branch
                    return

            #last symbol check   
            if '_' in currentTrie:
                final_res.append(current + [st])

        res = []
        iterate(s, res, [], trie)
        return [' '.join(r) for r in res]
