	public  List<String>  wordBreak(String s, List<String> wordDict) 
    {
        List<String> ans=new LinkedList<>();
        Set<String> wordDictSet = new HashSet<>(wordDict);

        Queue<Pair<Integer, StringBuilder>> queue = new LinkedList<>();     
        queue.add(new Pair(0, new StringBuilder()));

        while (!queue.isEmpty()) 
        {
            Pair<Integer, StringBuilder> cpair=queue.poll();
            int start=cpair.getKey();
            StringBuilder sb=cpair.getValue();

            for (int end=start+1; end<=s.length(); end++) 
            {
                if (wordDictSet.contains(s.substring(start, end))) 
                {
                    StringBuilder sbu=new StringBuilder(sb);
                    sbu.append(s.substring(start, end) + " ");

                    if (end == s.length())
                    {
                        ans.add(sbu.toString().trim());
                        continue;
                    }
                    queue.add(new Pair(end, sbu));
                }
            }
        }
        return ans;
    }


//	And here is actual solution for "139. Word Break"

	public boolean wordBreak(String s, List<String> wordDict) 
    {
        Set<String> wordDictSet = new HashSet<>(wordDict);
        boolean[] visited = new boolean[s.length()];

        Queue<Integer> queue = new LinkedList<>(); //keeps index    
        queue.add(0);

        while (!queue.isEmpty()) 
        {
            int start = queue.poll();
            if (visited[start]) 
                continue;

            for (int end=start+1; end<=s.length(); end++) 
            {
                if (wordDictSet.contains(s.substring(start, end))) 
                {
                    queue.add(end);
                    if (end == s.length()) 
                        return true;
                }
            }
            visited[start] = true;
        }
        return false;
    }

