class Solution {
public:

    class TrieNode{
    public:
        bool end;
        vector<TrieNode*> child;

        TrieNode(){
            end = false;
            child.assign(26,nullptr);
        }
    };

    class Trie{
    public:
        TrieNode* root;

        Trie(){
            root = new TrieNode();
        }

        void insert(string s){
            TrieNode* curr = root;
            for(auto& c:s){
                if(!curr->child[c-'a'])
                    curr->child[c-'a'] = new TrieNode();
                curr = curr->child[c-'a'];
            }
            curr->end = true;
        }

        bool search(string s){
            TrieNode* curr = root;
            for(auto& c:s){
                if(!curr->child[c-'a'])
                    return false;
                curr = curr->child[c-'a'];
            }
            return curr->end;
        }
    };

    Trie trie;
    vector<string> ans;

    void recurse(int index,string curr,string sentence,string s){
        if(index==s.length()){
            sentence.erase(sentence.begin());
            ans.push_back(sentence);
            return;
        }

        for(int i=index;i<s.length();i++){
            curr.push_back(s[i]);
            if(trie.search(curr))
                recurse(i+1,"",sentence+" "+curr,s);
        }
    }

    vector<string> wordBreak(string s, vector<string>& wordDict) {
        for(auto& str:wordDict){
            trie.insert(str);
        }
        recurse(0,"","",s);
        return ans;
    }
};
