        boolean isEndOfWord;
        public Trie(){
            for(int i=0;i<26;i++)
                children[i] = null;
            isEndOfWord = false;
        }
    }
    public void addWord(Trie root,String s){
        Trie node = root;
        for(int i=0;i<s.length();i++){
            int ch = s.charAt(i) - 'a';
            if(node.children[ch]==null)
                node.children[ch] = new Trie();
            node = node.children[ch];
        }
        node.isEndOfWord = true;
    }
    public boolean search(Trie root, String s){
        Trie node = root;
        for(int i=0;i<s.length();i++){
            int ch = s.charAt(i) - 'a';
            if(node.children[ch]==null)
                return false;
            node = node.children[ch];
        }
        return node.isEndOfWord;
    }

    List<String> list;
    public List<String> wordBreak(String s, List<String> wordDict) {
        Trie root = new Trie();
        for(String st:wordDict){
            addWord(root,st);
        }
        list = new ArrayList();
        findRemainingSubString(root,"",s,s.length());
        return list;
    }
