class Solution {

    // initialize cache - given a string -> holds possible combinations of valid sentences with spaces
    Map < String, List < String >> cache = new HashMap < > ();

    public List < String > wordBreak(String s, List < String > wordDict) {
        int maxLen = 0;

        // initialize dictionary in hashset (!remember lookup in list is O(n) not O(1))
        Set < String > dict = new HashSet < > (wordDict);

        // optimization - find the largest word length in dictionary
        for (String word: wordDict) {
            maxLen = Math.max(word.length(), maxLen);
        }

        return findValidSentences(s, dict, maxLen);
    }

    public List < String > findValidSentences(String s, Set < String > dict, int maxLen) {

        // if cache already has possibilities with the string return list
        if (cache.containsKey(s)) {
            return cache.get(s);
        }

        // else build
        List < String > result = new ArrayList < > ();

        // if string itself a valid word add - helps avoiding extra space for last iteration like "cats and dogs " - notice extra space at end
        if (dict.contains(s)) {
            result.add(s);
        }

        /* find from len 1 till n-1 length (full word already added)

           break word = left + right parts

           notice in for loop below: we are only checking for left part to a max of maxLen in dictionary since

           if dict = [car,rabbit,bus]

           word = rabbitcarbus

           word = [rabbit]carbus - iterating window limited to maxLen

           iterating beyond this is useless, since maximum length word that can be formed is rabbit in dict,
           there cant be a word greater than longest word in dictionary - doubles the overall performance as input scales

        */

        for (int i = 1; i <= maxLen && i < s.length(); i++) {

            // find left part
            String left = s.substring(0, i);

            // if left already in dictionary - find combinations with right part
            if (dict.contains(left)) {

                // find all possible valid combinations with right part
                List < String > rightSentences = findValidSentences(s.substring(i, s.length()), dict, maxLen);

                // combine both left + right = valid sentence combinations for give String s
                for (String right: rightSentences) {
                    result.add(left + " " + right);
                }

            }

        }

        // cache it for repeated usage
        cache.put(s, result);

        return result;
    }
}
