* Store dictionary words in set for quick access
* Helper function solves for the subproblems of the main problem
* vdp array to check if the dp value is available or not

```
class Solution {
public:
    unordered_set<string> dict;
    int n;
    string str;

    vector<string> helper(int i, vector<vector<string>> &dp, bool vdp[])
    {
        vector<string> tv;
        if(i==n)
        {
            return tv;
        }
        if(vdp[i])
        {
            return dp[i];
        }
        string ts = "";
        for(int k = i; k<n; k++)
        {
            ts+=str[k];
            if(dict.find(ts) == dict.end())
            {
                continue;
            }
            else
            {

                if(k+1==n)
                {
                    tv.push_back(ts);
                    continue;
                }
                vector<string> hv = helper(k+1, dp, vdp);
                for(int x = 0; x<hv.size(); x++)
                {
                    tv.push_back(ts+" "+hv[x]);
                }
            }
        }
        dp[i] = tv;
        vdp[i] = 1;
        return tv;
    }

    vector<string> wordBreak(string s, vector<string>& wordDict) {
        ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        n = s.length();
        str = s;
        for(int i =0; i<wordDict.size(); i++)
        {
            dict.insert(wordDict[i]);
        }
        vector<vector<string>> dp(n);
        bool vdp[n];
        memset(vdp,0,sizeof(vdp));
        return helper(0,dp,vdp);
    }
};
