func wordBreak(s string, wordDict []string) []string {
    dp := make(map[string][]string)    
    set := make(map[string]bool)
    for _, v := range wordDict {
        set[v] = true
    }
    return generate(s, dp, set)
}

func generate(s string, dp map[string][]string, set map[string]bool) []string{

    if val , ok := dp[s]; ok {
        return val
    }

    var output []string

    if len(s) == 0 {
        output = append(output, "")
        return output
    }

    var prefix string

    for i := 0; i < len(s); i++ {
        prefix += string(s[i])

        if _, ok := set[prefix]; ok {        
            var smallOutput []string = generate(s[i + 1 : ], dp, set)
            for _, val := range smallOutput {
                if len(val) > 0 {
                    output = append(output, prefix + " " + val)   
                }else {
                    output = append(output, prefix)
                }
            }
        }
    }

    dp[s] = output
    return output
}
