class Solution {

    fun wordBreak(s: String, wordDict: List<String>): List<String> {
        return buildWord(s, wordDict, hashMapOf())
    }

    private fun buildWord(
        text: String,
        wordDict: List<String>,
        map: HashMap<String, LinkedList<String>>
    ): List<String> {
        if (map.containsKey(text)) return map[text]!!

        val result = LinkedList<String>()
        if (text.isEmpty()) return result.apply { add("") }

        for (word in wordDict) {
            if (text.startsWith(word)) {
                buildWord(text.substring(word.length), wordDict, map)
                    .forEach { subWord -> result.add("$word${if (subWord.isEmpty()) "" else " "}$subWord") }
            }
        }

        map[text] = result
        return result
    }
}
