Base On Word Break I , I will use  vector<vector<string>> dp to keep the possible candidates

```
vector<string> wordBreak(string s, vector<string>& dict) {
        vector<string> res;
        if (dict.size() == 0) return res;
        int n = s.size();
        vector<vector<string>> dp(n+1, vector<string>());
        dp[0] = {" "};
        for (int i = 1; i <= n; ++i) {
            for (int j = i - 1; j >=0; --j) {
                if (!dp[j].empty()) {
                    string word = s.substr(j, i-j);
                    if (find(dict.begin(), dict.end(), word) != dict.end()) {
                        dp[i].push_back(word);
                    }
                }
            }
        }

        dfs(n, dp, "", res);

        return res;
    }

    void dfs(int loc, vector<vector<string>> dp, string word, vector<string> &res) {
        if (loc == 0) {
            res.push_back(word);
            return;
        }

        for (auto subword : dp[loc]) {
            int len = subword.size();
            string updateword = subword + (word.empty() ? "" : " ")  + word;
            dfs(loc - len, dp, updateword, res);
        }
    }
