class Solution {
public:
    vector<string> wordBreak(string s, vector<string>& wordDict) {
        unordered_map<string, vector<string>> memo;
        return dfs(s, memo, wordDict);
    }

    vector<string> dfs(string s, unordered_map<string, vector<string>>& memo, vector<string>& wordDict) {
        if (memo.find(s) != memo.end()) {
            return memo[s];
        }

        vector<string> results;
        if (s.size() == 0) {
            return {""};
        }

        for (string word : wordDict) {
            int len = word.size();
            if (len <= s.size()) {
                if (s.substr(0, len) != word) continue;
                string suffix = s.substr(len);

				//build results from suffix partition results - solve based on subproblems
                vector<string> segs = dfs(suffix, memo, wordDict);
                for (string seg : segs) {
                    results.push_back(seg.empty() ? word + seg : word + " " + seg);
                }
            }
        }

        memo[s] = results; //memoization
        return results;
    }
};
