class Solution {
public:
    bool searchMatrix(vector<vector<int>>& matrix, int target) {
        int n = matrix.size();
        int m = matrix[0].size();
        int startrow=0;
        int startcol= m-1;
        while(startrow < n && startcol >= 0)
        {
            int ele = matrix[startrow][startcol];
            if(ele==target)
            {
                return 1;
            }
            else if(ele<target)
            {
                startrow++;
            }
            else
            {
                startcol--;
            }

        }
        return 0;
    }
};
```

