BINARY SEARCH SOLUTION: O(nlogn)
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
```
class Solution {
public:
    bool searchMatrix(vector<vector<int>>& matrix, int target) {
        int m=matrix.size();
        if(m==0) return false;
        int n=matrix[0].size();
        if(n==0) return false;
        for(int i=0;i<m;i++){
            if(target<=matrix[i][n-1] and target>=matrix[i][0]){
                int left=0;
                int right=n-1;
                while(left<=right){
                    int mid=left +(right-left)/2;
                    if(matrix[i][mid]==target){
                        return true;
                    }
                    else if(target<matrix[i][mid]){
                        right=mid-1;
                    }
                    else left=mid+1;
                }
            }
        }
        return false;
    }
};
```
