class Solution {
public:
    bool binary_search(vector<int> &v1,int target){

        int low  = 0;
        int high = v1.size()-1;

        while(low<=high){
            int mid = low + (high-low)/2;

            if(v1[mid]<target){
                low = mid+1;
            }
            else if(v1[mid]>target){
                high = mid-1;
            }
            else{
                return true;
            }
        }

        return false;

