class Solution:
    def searchMatrix(self, matrix: List[List[int]], target: int) -> bool:

        def dq(loX, loY, hiX, hiY):
            if loX > hiX or loY > hiY: return False

            mX, mY = (loX+hiX) >> 1, (loY+hiY) >> 1

            if target < matrix[mX][mY]:
                return dq(loX, loY, mX-1, mY-1) or dq(loX, mY, mX-1, hiY) or dq(mX, loY, hiX, mY-1)
            elif target > matrix[mX][mY]:
                return dq(mX+1, mY+1, hiX, hiY) or dq(loX, mY+1, mX, hiY) or dq(mX+1, loY, hiX, mY)
            else:
                return True

        return dq(0, 0, len(matrix)-1, len(matrix[0])-1)
