class Solution {
public:
    bool searchMatrix(vector<vector<int>>& matrix, int target) {
        int m = matrix.size();
        int n = matrix[0].size();
        if(n < 1 || m < 1)
            return false;
        // Starting from TOP-RIGHT corner
        int row = 0;
        int col = n - 1;
        while((col >= 0) && (row < m)) {
            if(matrix[row][col] == target)
                return true;
            else if(matrix[row][col] > target)
                col--;
            else
                row++;
        }
        return false;
    }
}
