var findKthNumber = function (n, k) {
    function dfs (l, r) {
        let nn = BigInt(n);

        if (l > nn) {
            return 0n;
        }

        if (r > nn) {
            r = nn;
        }

        return r - l + 1n + dfs(l * 10n, r * 10n + 9n);
