    let kn = BigInt(--k), cu = 1;

    while (kn > 0) {
        let cs = dfs(BigInt(cu), BigInt(cu));

        if (cs <= kn) {
            kn -= cs;
            cu++;
        }
        else {
            kn -= 1n;
            cu *= 10;
        }
    }

    return cu;
};
