class Solution {
public:
    int findKthNumber(int n, int k) {
        string s = to_string(n);
        bool flag1=false;
        bool flag2=false;

        vector<int> tens;
        tens.push_back(1);
        for(int i=1;i<s.length();i++)
            tens.push_back(tens.back()+pow(10,i));

        string ans;
        int start=1;
        int ptr = 0;
        while(k>0 && ptr<s.length()){
            // try the character to be put next!
            int cr = -1;
            int ref = s[ptr]-'0';
            int left = s.length()-(ptr+1);
            // left will always be greater than or equal to 0!
            for(int i=start;i<10;i++){
                // How many characters with prefix (ans + i) less than n!
                cr = i;
                int take=0;
                if(flag1){
                    // ans is lexicographically smaller than prefix(n). In this case, we can always extend till full length
                    take = tens[left];
                }
                else if(flag2){
                    // ans is lexicographically larger than prefix(n). In this case, we cannot extend to full length, as the result will be greater then n
                    take = (left>0)?tens[left-1]:0;
                }
                else{
                    // ans is equal to a prefix of n. 
                    // 3 CASES. 
                    if(i<ref){
                        // What if the next character is less than s[i], string can be extended till the end!
                        take = tens[left];
                    }
                    else if(i == ref){
                        // What if equal, string can be extended till the end but for full length strings we can only allow numbers less than n
                        take = (left>0)?tens[left-1]:0;
                        take += (n%(int)pow(10,left))+1;
                    }
                    else{
                        // What if greater, string cannot be extended till full length!
                        take = (left>0)?tens[left-1]:0;
                    }
                }
                // cout << ptr << " " << i << " " << take << "\n";
                if(k-take>0){
                    k-=take;
                }
                else{
                    break;
                }
            }
            ans.push_back(cr+'0');
            k--;
            // cr is the current value in ans -  Set flags if necessary!
            if(!flag1 && !flag2){
                if(cr < ref){
                    flag1 = true;
                }
                else if(cr > ref){
                    flag2 = true;
                }
            }
            start = 0;
            ptr++;
        }
        // cout << ans << "\n";
        return stoi(ans);
    }
};
