class Solution(object):
    def findKthNumber(self, n, k): return self.f(1, k - 1 , n)

    def f(self, u, l, n):
        if l == 0: return u
        count = self.g(u, n) # how many nodes in the subtree rooted at u 
        return self.f(10 * u, l - 1, n) if count > l else self.f(u + 1, l - count, n)

    """
    how many nodes in the subtree rooted at u 
    for this consider v the tree right next to u (virtually)

    and count layer by layer

    """
    def g(self, u,n):
        count, v = 0, u + 1
        while(u <= n):
            count += min(v,n + 1) - u
            u *= 10
            v *= 10
        return count
