class Solution {
    public int findKthNumber(int n, int k) {
        Trie trie=new Trie();
        for(int i=1;i<=n;i++)
        {
            String word=String.valueOf(i);
            trie.insert(word,i);
        }
        trie.search(k,trie.root);
        return trie.ans;
    }
