class Trie{
    public static Node root;
    public int ans;
    public Trie() {
        ans=0;
        root=new Node(null);
    }
    public void insert(String word,int num) {
        Node node=root;
        for(char ch:word.toCharArray())
        {
            for(int i=0;i<10;i++){
                char c=(char)(i+'0');
                if(node.containsKey(c))continue;
                node.put(c,node);
            }
            node=node.get(ch);
        }
        node.setEnd(num);
    }
    public void search(int k,Node node){

        if(node.isEnd() && !node.mark){
            k--;
            node.setMark();
        }
        if(k==0){
            ans=node.number();
            return;
        }
        for(int i=0;i<10;i++)
        {
            if(node.links[i]!=null && node.links[i].par==node){
                search(k,node.links[i]);
                return;
            }
        }
        Node temp=node.par;
        node.par=null;
        search(k,temp);
    }
}
