    Node[] links;
    boolean end;
    int num;
    Node par;
    boolean mark;
    Node(Node par){
        this.mark=false;
        this.links=new Node[10];
        this.end=false;
        this.num=0;
        this.par=par;
    }
    boolean containsKey(char ch){
        return this.links[ch-'0']!=null;
    }
    void put(char ch,Node p){
        links[ch-'0']=new Node(p);
