    def findKthNumber(self, n: int, k: int) -> int:
        def steps(n, n1, n2):
            count = 0
            while n1 <= n:
                count += min(n + 1, n2) - n1
                n1 *= 10
                n2 *= 10
            return count

        curr = 1
        k -= 1
        while k > 0:
            count = steps(n, curr, curr + 1)
            if count <= k:
                curr += 1
                k -= count
            else:
                curr *= 10
                k -= 1
