def find_kth_number(n, k)
  n = n.to_s

  # count numbers from 1..n that start with a prefix
  count = -> prefix { 
    prefix==n ? 1 : n.start_with?(prefix) ? 1 + (?0..?9).sum{ count[prefix+_1] } :
      (0..(n.size-prefix.size).then{ prefix < n ? _1 : _1-1 }).sum{10**_1}
  }

  get = -> k,prefix=''{
    i = prefix[0] ? ?0 : ?1
    i.next! while k >= t=count[prefix+i] and k-=t
    k>0 ? get[k-1, prefix+i] : prefix+i
  }

  get[k-1].to_i
end
