class Solution:
    def findKthNumber(self, n: int, k: int) -> int:

        def findK(n, k, depth):
            if k == 0:
                return ""
            min_tree_depth = 0 if n < 10 else floor(math.log10(n)) 
            min_tree_size = 0 if min_tree_depth <= 0 else int("".join(["1" for _ in range(min_tree_depth)]))
            max_tree_size = int(str(min_tree_size)+"1")
            total_nodes = n
            if depth != 0:
                total_nodes = n
            child_size = [min_tree_size for _ in range(0, 10)]
            for child in range(10):
                if(child == 0 and depth == 0):
                    child_size[0] = 0
                    continue
                child_size[child] = max(min(max_tree_size, total_nodes - min_tree_size * (9 - child)), min_tree_size)
                total_nodes -= child_size[child]
                if(child_size[child] != max_tree_size):
                    break

            total = 0
            for i in range(10):
                if total + child_size[i] >= k:
                    return str(i) + findK(child_size[i] - 1, k - total - 1, depth + 1)
                total += child_size[i]


        return int(findK(n, k, 0))
