// package q_440

/**
  440. K-th Smallest in Lexicographical Order
  Hard

  579

  73

  Add to List

  Share
  Given two integers n and k, return the kth lexicographically smallest integer in the range [1, n].



  Example 1:

  Input: n = 13, k = 2
  Output: 10
  Explanation: The lexicographical order is [1, 10, 11, 12, 13, 2, 3, 4, 5, 6, 7, 8, 9], so the second smallest number is 10.
  Example 2:

  Input: n = 1, k = 1
  Output: 1


  Constraints:

  1 <= k <= n <= 109
*/

const (
	nilResult = -1
	digitBase = 10
)

type Solution struct{}

// countPrefixCombination
// count all the nums with prefix lees or equal to n
// include prefix itself
func (s *Solution) countPrefixCombination(n, prefix int) (retCount int) {
	if prefix > n {
		retCount = 0
		return retCount
	}
	if prefix == n {
		retCount = 1
		return retCount
	}

	retCount = 1 // prefix itself
	nextMin, nextMax := prefix, prefix
	for true {
		nextMin = nextMin * digitBase
		nextMax = nextMax*digitBase + digitBase - 1
		if n < nextMin {
			break
		}
		if n <= nextMax {
			retCount += n - nextMin + 1
			break
		} else {
			retCount += nextMax - nextMin + 1
		}
	}
	return retCount
}

// findKthNumberImpl
// make suer 1<=k<=n
func (s *Solution) findKthNumberImpl(n, k, prefix int) (retResult int) {
	if k == 0 {
		retResult = prefix
		return retResult
	}
	nextStartDigit := 0
	if prefix == 0 {
		nextStartDigit = 1
	}
	for ; nextStartDigit < digitBase; nextStartDigit++ {
		nextPrefix := prefix*digitBase + nextStartDigit
		nextPrefixCount := s.countPrefixCombination(n, nextPrefix)
		if nextPrefixCount >= k {
			// skip nextPrefix itself will not be count anymore
			// so k-1
			retResult = s.findKthNumberImpl(n, k-1, nextPrefix)
			break
		}
		k -= nextPrefixCount
	}
	return retResult
}

func (s *Solution) FindKthNumberImpl(n, k int) (retResult int) {
	if k <= 0 || n <= 0 || n < k {
		retResult = nilResult
		return retResult
	}
	retResult = s.findKthNumberImpl(n, k, 0)
	return retResult
}

func findKthNumber(n int, k int) (retResult int) {
	solution := Solution{}
	retResult = solution.FindKthNumberImpl(n, k)
	return retResult
}

