    int dfs(ll num) {
        if(num > n) return 0;
        k--;
        if(k == 0) return num;
        f(i,0,10) {
            ll ret = dfs(num*10+i);
            if(k == 0) return ret;
        }
        return 0;
    }

    int findKthNumber(int n, int k) {
        f(i,0,n) {
            ll ret = dfs(i);
            if(k == 0) return ret;
        }
        return 0;
    }
};
