* Optimised version: As determining the node count before hand is an `O(log n)` function unlike doing the DFS which actually take `O(n)` time. 
```
#define ll long long
#define vb vector<bool>
#define vi vector<int>
#define vl vector<long long>
#define vvb vector<vector<bool>>
#define vvi vector<vector<int>>
#define vvl vector<vector<long long>>
#define pii pair<int,int>
#define all(i) i.begin(),i.end()
#define f(i,s,e) for(int i = s; i < e; i++)
#define b(i,s,e) for(int i = s; i >= e; i--)
inline int getMid(int i,int j) { return i+(j-i)/2; }

class Solution {
public:
    ll n, k;

    ll steps(ll node) {
        ll l = node, r = node+1, steps = 0;
        while(l <= n) {
            steps += min(n+1,r)-l;
            l *= 10;
            r *= 10;
        } 
        return steps;
    }

    int findKthNumber(int n, int k) {
        this->n = n;
        this->k = k;
        ll node = 1;
        ll _k = k-1;
        while(_k) {
            // cout << "node " << node << endl;
            int stps = steps(node);
            // cout << "stps " << stps << endl;
            if(stps <= _k) {
                node++;
                _k -= stps;
            } else {
                node *= 10;
                _k--;
            }
        }

        return node;
    }
};
