class Solution:
    def findKthNumber(self, n: int, k: int) -> int:
        n_as_str = str(n)
        slots = len(n_as_str)
        # power(i) = 10 ^ i
        power = [1] * (slots + 1)
        for i in range(1, slots + 1):
            power[i] = power[i - 1] * 10
        # geometric(i) = 1 + 10 + ...+ 10 ^ i = (10^{i+1} - 1) / ( 10 - 1) 
        geometric = [1] * slots
        for i in range(1, slots):
            geometric[i] = (power[i + 1] - 1) // 9

        curr_prefix = 1
        free_slots = slots - 1

        def get_prefix_count(n: int, prefix: int) -> int:
            count = 0
            if free_slots > 0:
                count = geometric[free_slots - 1]
            if prefix < n // power[free_slots]:
                count += power[free_slots]
            if prefix > n // power[free_slots]:
                count += 0
            if prefix == n // power[free_slots]:
                count += n % power[free_slots] + 1
            return count

        while k > 0:
            prefix_count = get_prefix_count(n, curr_prefix)
            # Too few numbers in range [1, n] that start with curr_prefix, increment it
            if prefix_count < k:
                k -= prefix_count
                curr_prefix += 1
                continue
            k -= 1
            if k == 0:
                break
            curr_prefix = 10 * curr_prefix
            free_slots -= 1

        return curr_prefix
