class Solution {
    public int findKthNumber(int n, int k) {
        //当前遍历到的个数
        int cur = 1;
        int prefix = 1;
        while(cur < k){
            int temp = helper(n, prefix);
            if(temp + cur - 1 >= k){
                //证明在此前缀内
                prefix *= 10;
                cur ++;
            }else{
                cur += temp;
                //前缀增加1
                prefix ++;
            }
        }
        return prefix;
    }

    //统计这个前缀下的元素个数
    private int helper(int n, int prefix){
        long next = prefix + 1;
        //从当前元素开始，直至cur > n退出
        long cur = prefix;
        int count = 0;
        while(cur <= n){
            count += Math.min(next, n + 1) - cur;
            cur *= 10;
            next *= 10;
        }

        return count;
    }
}
