class Solution {
public:
    int get(int prefix, int n) {
        long long p = 1;
        auto A = to_string(n), B = to_string(prefix);
        int dt = A.size() - B.size();
        int ans = 0;
        for(int i=0; i<dt; i++) {
            ans += p;
            p *= 10;
        }
        A = A.substr(0, B.size());
        if(A > B) {
            ans += p;
        } else if(A == B) {
            ans += n - prefix*p + 1;
        }
        return ans;
    }
    int findKthNumber(int n, int k) {
        int prefix = 1;
        while(k > 1) {
            int cnt = get(prefix, n);
            if(cnt < k) {
                k -= cnt;
                prefix++;
            } else {
                k --;
                prefix *= 10;
            }
        }
        return prefix;
    }
};
