class Solution {
public:
    long _cnt;
    long _ans;
    void GetNums(long n, long prefix, int digits_left) {
        if(prefix>n || _cnt==0) return;
        if(--_cnt==0) {
            _ans=prefix;
            return;
        }
        if(digits_left) {
            long temp=prefix;
            int temp2=digits_left;
            while(temp2--) temp*=10;
            if(temp>n) digits_left--;
        }
        if(digits_left) {
            long max_cnt_nums=0, max_num=prefix+1, temp=digits_left;
            while(temp--) {
                max_cnt_nums=max_cnt_nums*10+1;
                max_num*=10;
            }
            max_cnt_nums*=10;
            if(max_cnt_nums<_cnt && max_num<n) {
                _cnt-=max_cnt_nums;
                return;
            }
        }
        for(int i=0; i<10; i++) GetNums(n, prefix*10+i, digits_left-1);
    }

    int findKthNumber(int n, int k) {
        _cnt=k, _ans=-1;
        int nDigits=0, temp=n;
        while(temp) {
            nDigits++;
            temp/=10;
        }
        for(int i=1; i<10 && _cnt; i++) GetNums(n, i, nDigits-1);
        return _ans;    
    }
};
