class Solution {
    public int findKthNumber(int n, int k) {
        int cur = 1;
        while (true) {
            if (k == 1) {
                return cur;
            }
            int steps = findSteps(cur, cur+1, n);
            if (k <= steps) {
                cur *= 10;
                k--;
            } else {
                cur++;
                k -= steps;
            }
        }
    }

    public int findSteps(long n1, long n2, long n) {
        long num = 0;
        while (n1 <= n) {
            if (n2 <= n) {
                num += (n2 - n1);
            } else {
                num += n - n1 + 1;
                break;
            }
            n1 *= 10;
            n2 *= 10;
        }
        return(int) num;
    }


}

